import React, { useEffect, useState } from 'react';
import './App.css';
import {
  API_BASE_URL,
  fetchCategories,
  fetchCustomers,
  fetchCustomerById,
  addCustomer as apiAddCustomer,
  updateCustomer as apiUpdateCustomer,
  deleteCustomer as apiDeleteCustomer
} from './api';
import { handleInputChange, resetForm } from './form';
import { renderCategoryRows, renderCustomerRows } from './data';

function App() {
  // State for categories and customers
  const [categories, setCategories] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [token, setToken] = useState('');
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [showError, setShowError] = useState('');
  // State for add form
  const [addForm, setAddForm] = useState({ nama: '', alamat: '', telepon: '' });
  const [addMessage, setAddMessage] = useState('');
  const [addError, setAddError] = useState('');
  // State for edit form
  const [editForm, setEditForm] = useState({ id: '', nama: '', alamat: '', telepon: '' });
  const [editMode, setEditMode] = useState(false);
  const [editMessage, setEditMessage] = useState('');
  const [editError, setEditError] = useState('');

  // Fetch categories from API
  useEffect(() => {
    fetchCategories()
      .then(res => setCategories(res.data))
      .catch(err => console.error('Error fetching categories:', err));
  }, []);

  // Fetch customers from API with token
  useEffect(() => {
    if (!token) return;
    fetchCustomers(token)
      .then(res => setCustomers(res.data))
      .catch(err => console.error('Error fetching customers:', err));
  }, [token]);

  // Function to fetch single customer detail by ID
  const showCustomer = (id) => {
    setShowError('');
    setSelectedCustomer(null);
    fetchCustomerById(id, token)
      .then(res => setSelectedCustomer(res.data))
      .catch(err => {
        setShowError('Failed to fetch customer detail.');
        setSelectedCustomer(null);
      });
  };

  // Function to handle add form input
  const handleAddInput = (e) => {
    handleInputChange(e, addForm, setAddForm);
  };

  // Function to add new customer
  const addCustomer = (e) => {
    e.preventDefault();
    setAddMessage('');
    setAddError('');
    apiAddCustomer(addForm, token)
      .then(res => {
        setAddMessage('Pelanggan berhasil ditambahkan!');
        resetForm(setAddForm, { nama: '', alamat: '', telepon: '' });
        // Refresh customer list
        fetchCustomers(token).then(res => setCustomers(res.data));
      })
      .catch(err => {
        if (err.response && err.response.data && err.response.data.message) {
          setAddError(err.response.data.message);
        } else {
          setAddError('Failed to add customer.');
        }
      });
  };

  // Function to delete customer by ID
  const deleteCustomer = (id) => {
    if (!window.confirm('Apakah Anda yakin ingin menghapus pelanggan ini?')) return;
    setAddMessage('');
    setAddError('');
    apiDeleteCustomer(id, token)
      .then(res => {
        setAddMessage(res.data.pesan || 'Pelanggan berhasil dihapus!');
        setCustomers(customers.filter(c => c.id !== id));
        if (selectedCustomer && selectedCustomer.id === id) setSelectedCustomer(null);
      })
      .catch(err => {
        if (err.response && err.response.data && err.response.data.pesan) {
          setAddError(err.response.data.pesan);
        } else {
          setAddError('Gagal menghapus pelanggan.');
        }
      });
  };

  // Function to open edit form with selected customer data
  const openEditForm = (customer) => {
    setEditForm({ id: customer.id, nama: customer.nama, alamat: customer.alamat, telepon: customer.telepon });
    setEditMode(true);
    setEditMessage('');
    setEditError('');
  };
  // Function to handle edit form input
  const handleEditInput = (e) => {
    handleInputChange(e, editForm, setEditForm);
  };
  // Function to update customer
  const updateCustomer = (e) => {
    e.preventDefault();
    setEditMessage('');
    setEditError('');
    apiUpdateCustomer(editForm.id, {
      nama: editForm.nama,
      alamat: editForm.alamat,
      telepon: editForm.telepon
    }, token)
      .then(res => {
        setEditMessage(res.data.pesan || 'Data pelanggan berhasil diperbarui!');
        setEditMode(false);
        setCustomers(customers.map(c => c.id === editForm.id ? { ...c, nama: editForm.nama, alamat: editForm.alamat, telepon: editForm.telepon } : c));
        if (selectedCustomer && selectedCustomer.id === editForm.id) {
          setSelectedCustomer({ ...selectedCustomer, nama: editForm.nama, alamat: editForm.alamat, telepon: editForm.telepon });
        }
      })
      .catch(err => {
        if (err.response && err.response.data && err.response.data.pesan) {
          setEditError(err.response.data.pesan);
        } else {
          setEditError('Gagal memperbarui pelanggan.');
        }
      });
  };
  // Function to cancel edit
  const cancelEdit = () => {
    setEditMode(false);
    resetForm(setEditForm, { id: '', nama: '', alamat: '', telepon: '' });
    setEditMessage('');
    setEditError('');
  };

  return (
    <div className="container mt-4">
      <h2>Data Kategori</h2>
      <table className="table table-bordered table-hover">
        <thead>
          <tr>
            <th>ID</th>
            <th>Kategori</th>
          </tr>
        </thead>
        <tbody>
          {renderCategoryRows(categories)}
        </tbody>
      </table>

      <h2 className="mt-5">Data Pelanggan</h2>
      {/* Input for API token */}
      <div className="mb-3">
        <label htmlFor="token" className="form-label">API Token</label>
        <input
          type="text"
          className="form-control"
          id="token"
          placeholder="Enter API token for pelanggan endpoint"
          value={token}
          onChange={e => setToken(e.target.value)}
        />
      </div>
      {/* Add Customer Form */}
      <div className="card mb-4">
        <div className="card-header">Tambah Pelanggan Baru</div>
        <div className="card-body">
          <form onSubmit={addCustomer}>
            <div className="mb-3">
              <label htmlFor="nama" className="form-label">Nama Pelanggan</label>
              <input
                type="text"
                className="form-control"
                id="nama"
                name="nama"
                value={addForm.nama}
                onChange={handleAddInput}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="alamat" className="form-label">Alamat</label>
              <input
                type="text"
                className="form-control"
                id="alamat"
                name="alamat"
                value={addForm.alamat}
                onChange={handleAddInput}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="telepon" className="form-label">Telepon</label>
              <input
                type="text"
                className="form-control"
                id="telepon"
                name="telepon"
                value={addForm.telepon}
                onChange={handleAddInput}
                required
              />
            </div>
            <button type="submit" className="btn btn-success">Tambah</button>
          </form>
          {addMessage && <div className="alert alert-success mt-3">{addMessage}</div>}
          {addError && <div className="alert alert-danger mt-3">{addError}</div>}
        </div>
      </div>
      <table className="table table-bordered table-hover">
        <thead>
          <tr>
            <th>ID</th>
            <th>Pelanggan</th>
            <th>Alamat</th>
            <th>Telepon</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {renderCustomerRows(customers, showCustomer, openEditForm, deleteCustomer)}
        </tbody>
      </table>
      {/* Show single customer detail */}
      {selectedCustomer && (
        <div className="card mt-4">
          <div className="card-header">Detail Pelanggan</div>
          <div className="card-body">
            <table className="table table-bordered">
              <tbody>
                <tr><th>ID</th><td>{selectedCustomer.id}</td></tr>
                <tr><th>Nama</th><td>{selectedCustomer.nama}</td></tr>
                <tr><th>Alamat</th><td>{selectedCustomer.alamat}</td></tr>
                <tr><th>Telepon</th><td>{selectedCustomer.telepon}</td></tr>
              </tbody>
            </table>
          </div>
        </div>
      )}
      {showError && (
        <div className="alert alert-danger mt-3">{showError}</div>
      )}
      {/* Edit Customer Form */}
      {editMode && (
        <div className="card mb-4">
          <div className="card-header">Edit Pelanggan</div>
          <div className="card-body">
            <form onSubmit={updateCustomer}>
              <div className="mb-3">
                <label htmlFor="edit-nama" className="form-label">Nama Pelanggan</label>
                <input
                  type="text"
                  className="form-control"
                  id="edit-nama"
                  name="nama"
                  value={editForm.nama}
                  onChange={handleEditInput}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="edit-alamat" className="form-label">Alamat</label>
                <input
                  type="text"
                  className="form-control"
                  id="edit-alamat"
                  name="alamat"
                  value={editForm.alamat}
                  onChange={handleEditInput}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="edit-telepon" className="form-label">Telepon</label>
                <input
                  type="text"
                  className="form-control"
                  id="edit-telepon"
                  name="telepon"
                  value={editForm.telepon}
                  onChange={handleEditInput}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary">Update</button>
              <button type="button" className="btn btn-secondary ms-2" onClick={cancelEdit}>Cancel</button>
            </form>
            {editMessage && <div className="alert alert-success mt-3">{editMessage}</div>}
            {editError && <div className="alert alert-danger mt-3">{editError}</div>}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
