import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  // State for categories and customers
  const [categories, setCategories] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [token, setToken] = useState(''); // Set your API token here
  const [selectedCustomer, setSelectedCustomer] = useState(null); // State for single customer detail
  const [showError, setShowError] = useState('');
  // State for add form
  const [addForm, setAddForm] = useState({ nama: '', alamat: '', telepon: '' });
  const [addMessage, setAddMessage] = useState('');
  const [addError, setAddError] = useState('');

  // Fetch categories from API
  useEffect(() => {
    axios.get('http://localhost:8000/api/kategori')
      .then(res => setCategories(res.data))
      .catch(err => console.error('Error fetching categories:', err));
  }, []);

  // Fetch customers from API with token
  useEffect(() => {
    if (!token) return;
    axios.get('http://localhost:8000/api/pelanggan', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => setCustomers(res.data))
      .catch(err => console.error('Error fetching customers:', err));
  }, [token]);

  // Function to fetch single customer detail by ID
  const showCustomer = (id) => {
    setShowError('');
    setSelectedCustomer(null);
    axios.get(`http://localhost:8000/api/pelanggan/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => setSelectedCustomer(res.data))
      .catch(err => {
        setShowError('Failed to fetch customer detail.');
        setSelectedCustomer(null);
      });
  };

  // Function to handle add form input
  const handleAddInput = (e) => {
    setAddForm({ ...addForm, [e.target.name]: e.target.value });
  };

  // Function to add new customer
  const addCustomer = (e) => {
    e.preventDefault();
    setAddMessage('');
    setAddError('');
    axios.post('http://localhost:8000/api/pelanggan', addForm, {
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    })
      .then(res => {
        setAddMessage('Pelanggan berhasil ditambahkan!');
        setAddForm({ nama: '', alamat: '', telepon: '' });
        // Refresh customer list
        axios.get('http://localhost:8000/api/pelanggan', {
          headers: { Authorization: `Bearer ${token}` }
        }).then(res => setCustomers(res.data));
      })
      .catch(err => {
        if (err.response && err.response.data && err.response.data.message) {
          setAddError(err.response.data.message);
        } else {
          setAddError('Failed to add customer.');
        }
      });
  };

  // Function to delete customer by ID
  const deleteCustomer = (id) => {
    if (!window.confirm('Apakah Anda yakin ingin menghapus pelanggan ini?')) return;
    setAddMessage('');
    setAddError('');
    axios.delete(`http://localhost:8000/api/pelanggan/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => {
        setAddMessage(res.data.pesan || 'Pelanggan berhasil dihapus!');
        // Remove deleted customer from state without reloading
        setCustomers(customers.filter(c => c.id !== id));
        // Clear selected customer if it was deleted
        if (selectedCustomer && selectedCustomer.id === id) setSelectedCustomer(null);
      })
      .catch(err => {
        if (err.response && err.response.data && err.response.data.pesan) {
          setAddError(err.response.data.pesan);
        } else {
          setAddError('Gagal menghapus pelanggan.');
        }
      });
  };

  return (
    <div className="container mt-4">
      <h2>Data Kategori</h2>
      <table className="table table-bordered table-hover">
        <thead>
          <tr>
            <th>ID</th>
            <th>Kategori</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.kategori}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2 className="mt-5">Data Pelanggan</h2>
      {/* Input for API token */}
      <div className="mb-3">
        <label htmlFor="token" className="form-label">API Token</label>
        <input
          type="text"
          className="form-control"
          id="token"
          placeholder="Enter API token for pelanggan endpoint"
          value={token}
          onChange={e => setToken(e.target.value)}
        />
      </div>
      {/* Add Customer Form */}
      <div className="card mb-4">
        <div className="card-header">Tambah Pelanggan Baru</div>
        <div className="card-body">
          <form onSubmit={addCustomer}>
            <div className="mb-3">
              <label htmlFor="nama" className="form-label">Nama Pelanggan</label>
              <input
                type="text"
                className="form-control"
                id="nama"
                name="nama"
                value={addForm.nama}
                onChange={handleAddInput}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="alamat" className="form-label">Alamat</label>
              <input
                type="text"
                className="form-control"
                id="alamat"
                name="alamat"
                value={addForm.alamat}
                onChange={handleAddInput}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="telepon" className="form-label">Telepon</label>
              <input
                type="text"
                className="form-control"
                id="telepon"
                name="telepon"
                value={addForm.telepon}
                onChange={handleAddInput}
                required
              />
            </div>
            <button type="submit" className="btn btn-success">Tambah</button>
          </form>
          {addMessage && <div className="alert alert-success mt-3">{addMessage}</div>}
          {addError && <div className="alert alert-danger mt-3">{addError}</div>}
        </div>
      </div>
      <table className="table table-bordered table-hover">
        <thead>
          <tr>
            <th>ID</th>
            <th>Pelanggan</th>
            <th>Alamat</th>
            <th>Telepon</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.nama}</td>
              <td>{item.alamat}</td>
              <td>{item.telepon}</td>
              <td>
                <button className="btn btn-info btn-sm" onClick={() => showCustomer(item.id)}>
                  Show
                </button>
                {' '}
                <button className="btn btn-danger btn-sm" onClick={() => deleteCustomer(item.id)}>
                  Hapus
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Show single customer detail */}
      {selectedCustomer && (
        <div className="card mt-4">
          <div className="card-header">Detail Pelanggan</div>
          <div className="card-body">
            <table className="table table-bordered">
              <tbody>
                <tr><th>ID</th><td>{selectedCustomer.id}</td></tr>
                <tr><th>Nama</th><td>{selectedCustomer.nama}</td></tr>
                <tr><th>Alamat</th><td>{selectedCustomer.alamat}</td></tr>
                <tr><th>Telepon</th><td>{selectedCustomer.telepon}</td></tr>
              </tbody>
            </table>
          </div>
        </div>
      )}
      {showError && (
        <div className="alert alert-danger mt-3">{showError}</div>
      )}
    </div>
  );
}

export default App;
